import React from 'react';

export default function ToolsTechnologies() {
  // Araçlar (Sol taraf)
const tools = [
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/vscode/vscode-original.svg", 
    alt: "VS Code", 
    label: "Visual Studio Code", 
    color: "from-blue-500 to-blue-600" 
  },
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/androidstudio/androidstudio-original.svg", 
    alt: "Android Studio", 
    label: "Android Studio", 
    color: "from-green-500 to-green-600" 
  },
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/jupyter/jupyter-original.svg", 
    alt: "Jupyter", 
    label: "Jupyter Notebook", 
    color: "from-orange-500 to-red-500" 
  },
  { 
    src: "https://raw.githubusercontent.com/ethereum/remix-ide/master/apps/remix-ide/src/assets/img/remix_logo.svg", 
    alt: "Remix IDE", 
    label: "Remix IDE", 
    color: "from-indigo-500 to-purple-600" 
  },
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/pycharm/pycharm-original.svg", 
    alt: "PyCharm", 
    label: "PyCharm", 
    color: "from-yellow-500 to-green-500" 
  },
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/linux/linux-original.svg", 
    alt: "Linux", 
    label: "Linux", 
    color: "from-gray-800 to-black" 
  },
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/git/git-original.svg", 
    alt: "Git", 
    label: "Git/GitHub", 
    color: "from-red-500 to-red-600" 
  },
  { 
    src: "https://www.svgrepo.com/show/354202/postman-icon.svg", 
    alt: "Postman", 
    label: "Postman", 
    color: "from-orange-500 to-red-500" 
  }
];

// Teknolojiler (Sağ taraf)
const technologies = [
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/docker/docker-original.svg", 
    alt: "Docker", 
    label: "Docker", 
    color: "from-blue-500 to-blue-600" 
  },
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/pandas/pandas-original.svg", 
    alt: "Pandas", 
    label: "Pandas", 
    color: "from-purple-500 to-purple-600" 
  },
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/numpy/numpy-original.svg", 
    alt: "NumPy", 
    label: "NumPy", 
    color: "from-blue-400 to-blue-500" 
  },
  { 
    src: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/firebase/firebase-plain.svg", 
    alt: "Firebase", 
    label: "Firebase", 
    color: "from-yellow-400 to-orange-500" 
  },
  { 
    src: "https://upload.wikimedia.org/wikipedia/commons/1/18/ISO_C%2B%2B_Logo.svg", 
    alt: "REST API", 
    label: "REST API", 
    color: "from-gray-600 to-gray-700" 
  },
  { 
    src: "https://raw.githubusercontent.com/devicons/devicon/master/icons/solidity/solidity-original.svg", 
    alt: "Web3", 
    label: "Web3", 
    color: "from-purple-600 to-indigo-600" 
  },
  { 
    src: "https://cryptologos.cc/logos/solana-sol-logo.svg", 
    alt: "BlockChain", 
    label: "BlockChain", 
    color: "from-gray-700 to-gray-800" 
  }
];

  const TechItem = ({ src, alt, label, color }) => (
    <div className="group p-6 rounded-2xl bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700 hover:border-slate-300 dark:hover:border-slate-600 hover:shadow-lg transition-all duration-300">
      <div className="flex flex-col items-center text-center space-y-3">
        <div className={`w-16 h-16 rounded-2xl bg-gradient-to-br ${color} flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300`}>
          <img src={src} alt={alt} className="h-8 w-8 filter brightness-0 invert" loading="lazy" />
        </div>
        <h3 className="font-semibold text-slate-900 dark:text-white text-sm group-hover:text-blue-600 dark:group-hover:text-blue-400 transition-colors">
          {label}
        </h3>
      </div>
    </div>
  );

  return (
    <section className="max-w-7xl mx-auto px-4">
      {/* Modern Section Header */}
      <div className="text-center mb-12">
        <div className="inline-flex items-center gap-3 mb-4">
          <div className="w-8 h-8 rounded-xl bg-gradient-to-br from-purple-500 to-blue-600 flex items-center justify-center">
            <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 7.172V5L8 4z" />
            </svg>
          </div>
          <span className="text-sm font-semibold text-purple-600 dark:text-purple-400 uppercase tracking-wider">Teknoloji</span>
        </div>
        <h2 className="text-3xl md:text-4xl font-bold text-slate-900 dark:text-white mb-4">
          Araçlar & Teknolojiler
        </h2>
        <p className="text-lg text-slate-600 dark:text-slate-400 max-w-2xl mx-auto">
          Geliştirme süreçlerimde kullandığım araçlar ve teknolojiler
        </p>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-2 gap-8 items-start">

        {/* Sol taraf - Araçlar */}
        <div className="space-y-6">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 rounded-2xl bg-gradient-to-br from-purple-500 to-indigo-600 flex items-center justify-center">
              <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 7.172V5L8 4z" />
              </svg>
            </div>
            <h3 className="text-2xl font-bold text-slate-900 dark:text-white">Araçlar</h3>
          </div>

          <div className="grid grid-cols-2 gap-4">
            {tools.map((tool) => (
              <TechItem key={tool.label} {...tool} />
            ))}
          </div>
        </div>

        {/* Sağ taraf - Teknolojiler */}
        <div className="space-y-6">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 rounded-2xl bg-gradient-to-br from-blue-500 to-cyan-600 flex items-center justify-center">
              <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 3v2m6-2v2M9 19v2m6-2v2M5 9H3m2 6H3m18-6h-2m2 6h-2M7 19h10a2 2 0 002-2V7a2 2 0 00-2-2H7a2 2 0 00-2 2v10a2 2 0 002 2zM9 9h6v6H9V9z" />
              </svg>
            </div>
            <h3 className="text-2xl font-bold text-slate-900 dark:text-white">Teknolojiler</h3>
          </div>

          <div className="grid grid-cols-2 gap-4">
            {technologies.map((tech) => (
              <TechItem key={tech.label} {...tech} />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}
